// 0. 如果使用模块化机制编程，导入Vue和VueRouter，要调用 Vue.use(VueRouter)
import Vue from 'vue'
import VueRouter from 'vue-router'
// 1. 定义 (路由) 组件。
// 可以从其他文件 import 进来
import Loading from '../components/Loading'
import Home from '../components/Home.vue'
import Make from "../components/Make.vue"
import Welcome from "../components/Welcome.vue"
import List from "../components/Testlist.vue"
import Login from '../components/Login.vue'
import App from '../App.vue'

Vue.use(VueRouter)
//获取原型对象上的push函数
const originalPush = VueRouter.prototype.push
//修改原型对象中的push方法
VueRouter.prototype.push = function push(location) {
	return originalPush.call(this, location).catch(err => err)
}
// 2. 定义路由
// 每个路由应该映射一个组件。

const routes = [{
		path: '/',
		name: 'App', //页面入口
		component: App //对应的组件模板
	},
	{
		path: '/login',
		name: 'login', //登录组件，
		component: Login //对应的组件模板
	},
	{
		path: '/Loading',
		name: 'Loading', //过渡组件，
		component: Loading //对应的组件模板
	},{
		path: '/EXAM',
		name: 'Home', //个人页面，
		component: Home, //对应的组件模板
		children: [{
				path: '/List',
				name: 'List', //试卷列表，
				component: List //对应的组件模板
			}, {
				path: '/make',
				name: 'Make', //创建试卷，
				component: Make //对应的组件模板
			},
			{
				path: '/Welcome',
				name: 'Welcome', //欢迎页面，
				component: Welcome //对应的组件模板
			}
		]
	}
]

// 3. 创建 router 实例，然后传 `routes` 配置
const router = new VueRouter({
	routes
})

export default router
